import React, { Component } from 'react';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Login from './components/Login/Login';
import Register from './components/Register/Register';
import './App.css';


const {ClarifaiStub, grpc} = require("clarifai-nodejs-grpc");

const stub = ClarifaiStub.grpc();

const metadata = new grpc.Metadata();
metadata.set("1f5d53f5cca6429a8ebfa96dc7aa6b30", "eff5029af1d94eb6aa9e19d10ca7a690");

stub.PostModelOutputs(
    {
      model_id: "53e1df302c079b3db8a0a36033ed2d15",
      inputs: [{data: {image: {url: "https://samples.clarifai.com/dog2.jpeg"}}}]
    },
    metadata,
    (err, response) => {
        if (err) {
            console.log("Error: " + err);
            return;
        }

        if (response.status.code !== 10000) {
            console.log("Received failed status: " + response.status.description + "\n" + response.status.details);
            return;
        }

        console.log("Predicted concepts, with confidence values:")
        for (const c of response.outputs[0].data.concepts) {
            console.log(c.name + ": " + c.value);
        }
    }
  );

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
      route: 'login',
      isSignedIn: false
    }
  }

  calculateFaceLocation = (data) => {
    const clarifaiFace = JSON.parse(data,null,2).outputs[0].data.regions[0].region_info.bounding_box;
    // const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById("inputimage");
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height)
    };
  }

  displayFaceBox = (box) => {
    this.setState({box: box}); 
  }

  //input property of the app
  onInputChange = (event) => {
    this.setState({input: event.target.value});
  }

  onButtonSubmit = () => {
    const raw = JSON.stringify({
      user_app_id: {
        user_id: "binelias",
        app_id: "dc294004d3844cb5a11a8d0ab6fb252c"
      },
      inputs: [
        {
          data: {
            image: {
              url: this.state.input
            },
          },
        },
      ],
    });

    fetch(
      "https://api.clarifai.com/v2/models/53e1df302c079b3db8a0a36033ed2d15/outputs/",
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            Authorization: "1f5d53f5cca6429a8ebfa96dc7aa6b30"
          },
          body: raw,
        }
      )
      .then((response) => response.text())
      .then((result) => this.displayFaceBox(this.calculateFaceLocation(result)))
      .catch((err) => console.log("error", err));
  }

  onRouteChange = (route) => {
    if (route === 'signout') {
      this.setState({isSignedIn: false})
    } else {
      this.setState({isSignedIn: true})
    }
    this.setState({route: 'home'});
  }

  render() {
    const { isSignedIn, imageUrl, route, box } = this.state;
    return (
      <div className="App">
        <Navigation isSignedIn = {isSignedIn} onRouteChange={this.onRouteChange}/>
        { route === 'home' 
          ? <div>
              <Logo />
              <Rank />
              <ImageLinkForm 
              onInputChange = {this.onInputChange}
              onButtonSubmit = {this.onButtonSubmit}
              />
              <FaceRecognition 
              box={box} 
              imageUrl = {imageUrl} 
              />
            </div>
          : (
            route === 'signin' 
            ? <Login onRouteChange = {this.onRouteChange} />
            : <Register onRouteChange = {this.onRouteChange} />
          )
        }
      </div>
    );
  }
}

export default App;
